spec.query:
sum by (cluster) (
  (
    rate(radosgw_bytes_sent{tenant="all", user="all"}[$__rate_interval]) +
    rate(radosgw_bytes_received{tenant="all", user="all"}[$__rate_interval])
  ) or (
    rate(radosgw_bytes_sent{tenant="all", user="all"}[$__rate_interval]) or
    rate(radosgw_bytes_received{tenant="all", user="all"}[$__rate_interval])
  ) 
)

label_replace(
  sum by (pod) (
    rate(radosgw_total_requests{user="all", tenant="all", method="all", http_status="all"}[$__rate_interval])
  ),
  "pod_id", "$1", "pod", "rook-ceph-rgw-ceph-objectstore-.*-[0-9]-(.*)"
)

label_replace(
  sum by (pod) (
    rate(radosgw_errors_total{user="all", tenant="all", bucket="all"}[$__rate_interval])
  ),
  "pod_id", "$1", "pod", "rook-ceph-rgw-ceph-objectstore-.*-[0-9]-(.*)"
